jQuery(function(e){

    jQuery('.navbar-toggler').on('click', function () {
    
        var container = document.querySelector('#header-nav');

        if(container.classList.contains("hidden")){
        
            jQuery('.collapse').toggleClass('hidden').toggleClass('show');
            
            jQuery(container).toggleClass('isclosing').toggleClass('show-finish');;
            
            setTimeout(function()  {
                
                jQuery(container).toggleClass('show');
                    
            }, 500);
        
            setTimeout(function()  {
                
                jQuery(container).toggleClass('isclosing');
                    
            }, 1000);

        } else {
            
            jQuery('.collapse').toggleClass('hidden').toggleClass('show');
            
            jQuery(container).toggleClass('show').toggleClass('isopening');
        
            setTimeout(function()  {
                
                jQuery(container).addClass('show-finish');
    
                    
            }, 500);
        
            setTimeout(function()  {
                
                jQuery(container).toggleClass('isopening');
                    
            }, 1000);

        }
        
    });

});
 








 
